package sample;

public class Test2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
for(int i=0;i<5;i++){
	for(int j=0;j<a-i;j++){
		System.out.print(" ");
		
	}
	for(int k=0;k<(2*i-1);k++){
		System.out.print("*");
	}
	System.out.println();	
		
}
	}

}
